import{_ as s,o,a as t,c as a}from"./index-ee5475a0.js";const c={},e={class:"main-about"},_=a('<div class="about-block"><h1 class="h1-about">О нас</h1><p class="about-p"> Рады приветствовать вас на официальном сайте компании «The Cosmos», где вы можете купить необычные, вкусные и ароматные и изысканные настойки и ликеры. Ореховые, травяные, фруктовые, ягодные – в каталоге магазина представлена продукция на любой вкус. Наши товары по достоинству оценят все, кто предпочитает натуральность и экологичность продуктов. «The Cosmos» предлагает широкий ассортимент продукции, которая придется по вкусу даже самым искушенным гурманам. Сочетание оригинальных вкусов, использование экзотических и традиционных, привычных украинцам фруктов, ягод и трав, профессиональный микс разных ингредиентов, тщательно подобранных нашими мастерами, – все это позволит вам насладиться незабываемым вкусом каждой настойки представленной на полках магазина.</p></div><div class="about-block2"><h2 class="about-h2">Настойки</h2><p class="about-p2"> На полках нашего магазина вы найдете разнообразные настойки собственного производства. Уникальные рецепты и особые технологии производства позволяют создавать неповторимые настойки из разных ягод, трав, фруктов. Одной из таких технологий является мацерация, которая представляет собой способ приготовления спиртованных настоев путем двукратного настаивания ингредиентов при обычной температуре. Затем добавляются секретные ингредиенты, а также сахар, мед и дистиллированная вода. Для производства той или иной настойки используется высококачественное и только натуральное сырье: гранат, абрикос, имбирь, брусника, земляника, айва, вишня и многое другое. Все настойки произведены с соблюдением технологии по эксклюзивным рецептам.</p></div>',2),n=[_];function i(l,d){return o(),t("div",e,n)}const r=s(c,[["render",i]]);export{r as default};
